import { getPopup } from 'sweetalerttwo/src/utils/dom/getters.js'
import { renderActions } from 'sweetalerttwo/src/utils/dom/renderers/renderActions.js.js'
import { renderCloseButton } from 'sweetalerttwo/src/utils/dom/renderers/renderCloseButton.js.js'
import { renderContainer } from 'sweetalerttwo/src/utils/dom/renderers/renderContainer.js.js'
import { renderContent } from 'sweetalerttwo/src/utils/dom/renderers/renderContent.js.js'
import { renderFooter } from 'sweetalerttwo/src/utils/dom/renderers/renderFooter.js.js'
import { renderIcon } from 'sweetalerttwo/src/utils/dom/renderers/renderIcon.js.js'
import { renderImage } from 'sweetalerttwo/src/utils/dom/renderers/renderImage.js.js'
import { renderPopup } from 'sweetalerttwo/src/utils/dom/renderers/renderPopup.js.js'
import { renderProgressSteps } from 'sweetalerttwo/src/utils/dom/renderers/renderProgressSteps.js.js'
import { renderTitle } from 'sweetalerttwo/src/utils/dom/renderers/renderTitle.js.js'

/**
 * @param {SweetAlert2} instance
 * @param {SweetAlertOptions} params
 */
export const render = (instance, params) => {
  renderPopup(instance, params)
  renderContainer(instance, params)

  renderProgressSteps(instance, params)
  renderIcon(instance, params)
  renderImage(instance, params)
  renderTitle(instance, params)
  renderCloseButton(instance, params)

  renderContent(instance, params)
  renderActions(instance, params)
  renderFooter(instance, params)

  if (typeof params.didRender === 'function') {
    params.didRender(getPopup())
  }
}
